import React from "react";

export default function Chef() {
  const initialList = [
    {
      id: "a",
      name: "Buzz Lightyear",
      description: "cheese with pepporoni"
    },
    {
      id: "b",
      name: "Woody",
      description: "bacon, sausage, and chicken"
    },
    {
      id: "c",
      name: "Andy's Room",
      description: "peppers, onions, and spiach"
    },
    {
      id: "c",
      name: "Wild Bill",
      description: "bbq pork, chicken, and hot sauce"
    },
    {
      id: "c",
      name: "Rex",
      description: "peppers, chicken, and salami"
    }
  ];

  const [list, setList] = React.useState(initialList)
  const [name, setName] = React.useState('')

  function handleChange(event) {
    setName(event.target.value)
    // input field state
  }

  function handleRemove(id){
    const newList = list.filter((item) => item.id !== id);

    setList(newList)
  }

  function handleAdd(){
    const newList = list.concat({name})

    setList(newList)
    // add item to list
  }
  return (
    <div>
      <h1>Welcome Chef!</h1>
      <h2>First, take a look at our list of available Pizzas, and their respective toppings</h2>
  
      <ul>
        {list.map((item) =>(
          <li key={item.id}>{item.name}<br></br>{item.description}</li>
        ))}
        
