import React, {useState, useEffect} from "react";
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton';

export default function App() {
  const [toppings, setToppings] = useState(() => {
    const savedToppings = localStorage.getItem("toppings");
    if (savedToppings) {
      return JSON.parse(savedToppings);
    } else {
      return [];
    }
  });
  const [topping, setTopping] = useState("");
  // boolean state to know if we are editing (this will let us display
  // different inputs based on a condition (conditional rendering)
  const [isEditing, setIsEditing] = useState(false);
  // object state to set so we know which topping item we are editing
  const [currentTopping, setCurrentTopping] = useState({});

  useEffect(() => {
    localStorage.setItem("toppings", JSON.stringify(toppings));
  }, [toppings]);

  function handleInputChange(e) {
    setTopping(e.target.value);
  }

  // function to get the value of the edit input and set the new state
  function handleEditInputChange(e) {
    // set the new state value to what's currently in the edit input box
    setCurrentTopping({ ...currentTopping, text: e.target.value });
    console.log(currentTopping);
  }

  function handleFormSubmit(e) {
    e.preventDefault();

    if (topping !== "") {
      setToppings([
        ...toppings,
        {
          id: toppings.length + 1,
          text: topping.trim()
        }
      ]);
    }

    setTopping("");
  }

  function handleEditFormSubmit(e) {
    e.preventDefault();

    handleUpdateTopping(currentTopping.id, currentTopping);
  }

  function handleDeleteClick(id) {
    const removeItem = toppings.filter((topping) => {
      return topping.id !== id;
    });
    setToppings(removeItem);
  }

  // function to edit a topping item
  function handleUpdateTopping(id, updatedTopping) {
    // here we are mapping over the toppings array - the idea is check if the topping.id matches the id we pass into the function
    // if the id's match, use the second parameter to pass in the updated topping object
    // otherwise just use old topping
    const updatedItem = toppings.map((topping) => {
      return topping.id === id ? updatedTopping : topping;
    });
    // set editing to false because this function will be used inside a onSubmit function - which means the data was submited and we are no longer editing
    setIsEditing(false);
    // update the toppings state with the updated topping
    setToppings(updatedItem);
  }

  // function to handle when the "Edit" button is clicked
  function handleEditClick(topping) {
    // set editing to true
    setIsEditing(true);
    // set the currentTopping to the topping item that was clicked
    setCurrentTopping({ ...topping });
  }

  return (
    <div className="App">
      <h1>Current Pizzas on our Menu</h1>
      <h2>
        <ul className="pizzaName">
          <li>
            Buzz Lightyear (cheese with pepporoni)
          </li>
          <br></br>
          <li>
          Woody (bacon, sausage, and chicken)
          </li>
          <br></br>
          <li>
          Andy's Room (peppers, onions, and spiach)          </li>
          <br></br>
          <li>
          Wild Bill (bbq pork, chicken, and hot sauce)
          </li>
          <br></br>
          <li>
          Rex (peppers, chicken, and salami)
          </li>
        </ul>
      </h2>
      {/* We need to conditionally render different inputs based on if we are in editing mode */}
      {isEditing ? (
        // if we are editing - display the edit topping input
        // make sure to add the handleEditFormSubmit function in the "onSubmit" prop
        <form onSubmit={handleEditFormSubmit}>
          {/* we've added an h2 element */}
          <h2>Edit Topping</h2>
          {/* also added a label for the input */}
          <label htmlFor="editTopping">Edit topping: </label>
          {/* notice that the value for the update input is set to the currentTopping state */}
          {/* also notice the handleEditInputChange is being used */}
          <input
            name="editTopping"
            type="text"
            placeholder="Edit topping"
            value={currentTopping.text}
            onChange={handleEditInputChange}
          />
          {/* here we added an "update" button element - use the type="submit" on the button which will still submit the form when clicked using the handleEditFormSubmit function */}
          <button type="submit" className="buttons">Update</button>
          {/* here we added a "Cancel" button to set isEditing state back to false which will cancel editing mode */}
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </form>
      ) : (
        // if we are not editing - display the add topping input
        // make sure to add the handleFormSubmit function in the "onSubmit" prop
        <form onSubmit={handleFormSubmit}>
          {/* we've added an h2 element */}
          <h2>Create a new pizza! Enter the name of the pizza below, with toppings in parenthesis</h2>
          {/* also added a label for the input */}
          <label htmlFor="topping">Pizza Name: </label>
          {/* notice that the value is still set to the topping state */}
          {/* also notice the handleInputChange is still the same */}
          <input
            name="topping"
            type="text"
            placeholder="Name of pizza"
            value={topping}
            onChange={handleInputChange}
          />
          {/* here we just added a "Add" button element - use the type="submit" on the button which will still submit the form when clicked using the handleFormSubmit function */}
          <button type="submit" className="buttons">Add</button>
        </form>
      ) }

      <ul className="pizzaName">
        {toppings.map((topping) => (
          <li key={topping.id}>
            {topping.text}
            {/* we are passing the entire topping object to the handleEditClick function*/}
            <button className="buttons" onClick={() => handleEditClick(topping)}>Edit</button>
            <button className="buttons" onClick={() => handleDeleteClick(topping.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
















