import React, {useState, useEffect} from "react";

export default function Chef() {
  const [toppings, setToppings] = useState(() => {
    const savedToppings = localStorage.getItem("toppings")
    if(savedToppings){
      return JSON.parse(savedToppings)
    } else {
      return[]
    }
  })
  const initialList = [
    {
      id: "a",
      name: "Buzz Lightyear",
      description: "cheese with pepporoni"
    },
    {
      id: "b",
      name: "Woody",
      description: "bacon, sausage, and chicken"
    },
    {
      id: "c",
      name: "Andy's Room",
      description: "peppers, onions, and spiach"
    },
    {
      id: "c",
      name: "Wild Bill",
      description: "bbq pork, chicken, and hot sauce"
    },
    {
      id: "c",
      name: "Rex",
      description: "peppers, chicken, and salami"
    }
  ];

  const [list, setList] = React.useState(initialList)
  const [topping, setTopping] = React.useState('')
  const [description, setDescription] = React.useState('')
  const [isEditing, setIsEditing] = useState(false);
  const [currentTopping, setCurrentTopping] = useState({})

  useEffect(() => {
    localStorage.setItem("toppings", JSON.stringify(toppings));
  }, [toppings]);

  function handleChange(event) {
    setTopping(event.target.value)
    // input field state
  }

  function handleDeleteClick(id) {
    const removeItem = toppings.filter((topping) => {
      return topping.id !== id;
    });
    setList(removeItem);
  }


  function handleAdd(){
    const newList = list.concat({name})

    setToppings(newList)
    // add item to list
  }

  function addTopping(){
    const newTopping = list.concat({description})

    setList(newTopping)
  }
  return (
    <div>
      <h1>Welcome Chef!</h1>
      <h2>First, take a look at our list of available Pizzas, and their respective toppings</h2>
  
      <ul>
        {list.map((item) =>(
          <li key={item.id}>{item.name}<br></br>{item.description}
          <button onClick={()=> handleRemove(name.id)}>delete</button>
          </li>
          
        ))}
        
      </ul>
      <input type="text" placeholder="name of pizza" value={name} onChange={handleChange}/>
      <button type="button" onClick={handleAdd}>
        Add Pizza
      </button>
      <input type="text" placeholder="toppings on pizza" value={description} onChange={handleToppings}/>
      <button type="button" onClick={addTopping}>
        Add Toppings
      </button>
     
    </div>
  )
}
